import React, { Component } from 'react';
import axios from 'axios';

class HomePage extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            date1:'',
            time1:''
        };
    }

    updateTime(){
        axios.get("/time")
        .then(response => {
            console.log("State: "+response);
            this.setState({
                date1: response.data.date1,
                time1: response.data.time1,
            });                    
        })
        .catch(err => {
            console.log(" Error : "+err.message);
        })
    }

    componentDidMount() {
        this.updateTime();    
    }

    componentWillMount(){
        this.resettime=setInterval(()=>this.updateTime(),(600000));
    }

    render() { 
    return ( <div className="myTime">
    <center>
        <h2>Date: {this.state.date1}</h2> 
        <h2>Time: {this.state.time1}</h2>

    </center>    
    </div> );
    }
}

export default HomePage;
