import React from 'react';
import axios from 'axios';
import './App.css';

class App extends React.Component{

state={
  title:'',
  body:'',
  posts:[]
};

componentDidMount = () => {
  this.getBlogPost();
};

getBlogPost = () =>{

  axios.get('/api')
    .then((response) =>{
      const data = response.data;
      this.setState({ posts:data });
      console.log('Data has been received!!');
    })
    .catch(() => {
     alert('Error retrieving data!!');
    });
}

handleChange = ({target}) =>{          //if a change is occurred
  const {name,value} = target;
  this.setState( {[name] : value} );      //to change the state from null to a value
};

  submit = (event) => {
    event.preventDefault();         //so the browser does not refresh the page

    const payload = {               //contains the data which has been entered
      title:this.state.title,
      body:this.state.body
    };


  axios({                    //makes http call
    url:'/api/save',     // changes made to 'http://localhost:3001/api/save' after adding proxy for cors
    method:'POST',
    data:payload
  })
                //promises
    .then(() =>{
      console.log('Data has been sent to the server');
      this.resetUserInputs();
      this.getBlogPost();
    })
    .catch( () =>{
      console.log('Internal server error');
    });                    

  };          //------end of submit

  //to reset the form after the data has been sent
  resetUserInputs = () =>{
    this.setState({
      title:'',
      body:''
    });
  };

  displayBlogPost = (posts) => {

    if(!posts.length) return null;

    return posts.map((post , index) => (
      <div key={index} className="blog-post-display">
        <h3>{post.title}</h3>
        <p>{post.body}</p>
      </div>
    ));

  };

  render(){

    console.log('State: ', this.state);

    //JSX
    return(
      <div className="app">
        <h2>Welcome to LP3 Assignment!</h2>

        <div className="blog-post">
        {this.displayBlogPost(this.state.posts)}
        </div>
      </div>
    );
  }
}


export default App;
