import React from 'react';
import axios from 'axios';
import './App.css';

class App extends React.Component{

state={
  date1:'',
  time1:'',
  posts:[]
};

componentDidMount = () => {
  this.getBlogPost();
};

getBlogPost = () =>{

  axios.get('/')
    .then((response) =>{
      const data = response.data;
      this.setState({ posts:data });
      console.log('Data has been received!!');
    })
    .catch(() => {
     alert('Error retrieving data!!');
    });
}



  submit = () => {
    const payload = {               //contains the data which has been entered
      date1:this.state.date1,
      time1:this.state.time1
    };


  axios({                    //makes http call
    url:'http://localhost:3001/save',     // changes made to 'http://localhost:3001/api/save' after adding proxy for cors
    method:'POST',
    data:payload
  })
                //promises
    .then(() =>{
      console.log('Data has been sent to the server');
      //this.resetUserInputs();
      this.getBlogPost();
    })
    .catch( () =>{
      console.log('Internal server error');
    });                    

  };          //------end of submit

  //to reset the form after the data has been sent
/*  resetUserInputs = () =>{
    this.setState({
      title:'',
      body:''
    });
  };
*/
  displayBlogPost = (posts) => {

    if(!posts.length) return null;

    return posts.map((post , index) => (
      <div key={index} className="blog-post-display">
        <h3>{post.date1}</h3>
        <p>{post.time1}</p>
      </div>
    ));

  };

  render(){

    console.log('State: ', this.state);

    //JSX
    return(
      <div className="app">
        <h2>Welcome to LP3 Assignment!</h2>

        <div className="blog-post">
        {this.displayBlogPost(this.state.posts)}
        </div>
      </div>
    );
  }
}


export default App;
